function [Sdot,SMean,tau] = FID_Simulation5x5_ConstantCoupling()    % Autor: Rodrigo N. Alcalá M.    % Licencia: General Public License V3    % Simulación FID en CaF_2, sistema 5x5        % Constantes:    r0 = 2.72e-10; % [m]    g = 25166.2; % [Rad/sOe]        J_x = -0.41;    J_y = -0.41;    Jmn = -0.41;        leftFactor = 5;    rightFactor = 1;    upFactor = 5;    downFactor = 1;            Sdot = [];    J = [];        SMean = iteratedSMean(iter=500);    S = SMean;        for n = 1:5        for m = 1:5                        J(m,n)= Jmn;                        left  = (n-1);            right = (n+1);            up = (m-1);            down = (m+1);                        if left == 0                                left = 5;                                xFactor = (S(1,m))*(Jmn)*(2*S(2,n) + S(2,left) + S(2,right));                yFactor = (S(2,m))*(Jmn)*(2*S(1,n) + S(1,left) + S(1,right));                            Sdot(m,n) = xFactor - yFactor;                                elseif right == 6                                right = 1;                                xFactor = (S(1,m))*(Jmn)*(2*S(2,n) + S(2,left) + S(2,right));                yFactor = (S(2,m))*(Jmn)*(2*S(1,n) + S(1,left) + S(1,right));                                Sdot(m,n) = xFactor - yFactor;
            else                xFactor = (S(1,m))*(Jmn)*(2*S(2,n) + S(2,left) + S(2,right));                yFactor = (S(2,m))*(Jmn)*(2*S(1,n) + S(1,left) + S(1,right));                Sdot(m,n) = xFactor - yFactor;            end                    endfor    endfor        tau = characteristicTime_FIDSimulation(SMean,J);    end